import React, { useState } from 'react';
import axios from 'axios';

const OperationForm = () => {
    const [formData, setFormData] = useState({
        client_name: '',
        operation_name: '',
        business_link: '',
        description: '',
        financial_instrument: '',
        sector: '',
        sub_sector: '',
        country: ''
    });

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post('http://localhost:8000/api/operations/', formData)
            .then(response => {
                console.log(response.data);
            })
            .catch(error => {
                console.error('There was an error!', error);
            });
    };

    return (
        <form onSubmit={handleSubmit}>
            <input type="text" name="client_name" value={formData.client_name} onChange={handleChange} placeholder="Client Name" required />
            <input type="text" name="operation_name" value={formData.operation_name} onChange={handleChange} placeholder="Operation Name" required />
            <input type="url" name="business_link" value={formData.business_link} onChange={handleChange} placeholder="Business Link" />
            <textarea name="description" value={formData.description} onChange={handleChange} placeholder="Description" required />
            <input type="text" name="financial_instrument" value={formData.financial_instrument} onChange={handleChange} placeholder="Financial Instrument" required />
            <input type="text" name="sector" value={formData.sector} onChange={handleChange} placeholder="Sector" required />
            <input type="text" name="sub_sector" value={formData.sub_sector} onChange={handleChange} placeholder="Sub Sector" required />
            <input type="text" name="country" value={formData.country} onChange={handleChange} placeholder="Country" required />
            <button type="submit">Submit</button>
        </form>
    );
};

export default OperationForm;
