// src/components/OperationForm.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const OperationForm = () => {
    const [operation, setOperation] = useState({
        client_name: '',
        operation_name: '',
        business_link: '',
        description: '',
        financial_instrument: '',
        sector: '',
        sub_sector: '',
        country: ''
    });
    const [operations, setOperations] = useState([]);
    const [selectedId, setSelectedId] = useState('');
    const [message, setMessage] = useState('');

    const financialInstruments = [
        'Construcción de plantas de biomasa (bagazo)',
        'Desarrollo de infraestructura TICs',
        'Eficiencia Energética',
        'Enfoques top-down y bottom-up de servicios de agua, saneamiento y electricidad en asentamientos informales o de bajos ingresos',
        'Explotación de eucalipto',
        'Financiamiento a Microempresas (visión de género)',
        'Financiamiento a Pymes / Garantías',
        'Financiamiento de construcción de líneas de metro',
        'Financiamiento de construcción de puertos',
        'Financiamiento de Educación Superior',
        'Financiamiento de infraestructura de irrigación',
        'Financiamiento de infraestructura educativa',
        'Financiamiento para construcción ferroviaria',
        ''
    ];

    useEffect(() => {
        // Fetch existing operations to populate the dropdown
        axios.get('http://localhost:8000/api/operations/')
            .then(response => {
                setOperations(response.data);
            })
            .catch(error => console.error('Error fetching operations:', error));
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setOperation({ ...operation, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (selectedId) {
            // Update existing operation
            axios.put(`http://localhost:8000/api/operations/${selectedId}/`, operation)
                .then(response => {
                    console.log('Operation updated:', response.data);
                    setMessage(`Operation with ID ${response.data.id} updated successfully.`);
                    setOperation({
                        client_name: '',
                        operation_name: '',
                        business_link: '',
                        description: '',
                        financial_instrument: '',
                        sector: '',
                        sub_sector: '',
                        country: ''
                    });
                    setSelectedId('');
                })
                .catch(error => console.error('Error updating operation:', error));
        } else {
            // Create new operation
            axios.post('http://localhost:8000/api/operations/', operation)
                .then(response => {
                    console.log('Operation created:', response.data);
                    setMessage(`Operation created successfully with ID ${response.data.id}.`);
                    setOperation({
                        client_name: '',
                        operation_name: '',
                        business_link: '',
                        description: '',
                        financial_instrument: '',
                        sector: '',
                        sub_sector: '',
                        country: ''
                    });
                })
                .catch(error => console.error('Error creating operation:', error));
        }
    };

    const handleSelectChange = (e) => {
        const selectedId = e.target.value;
        setSelectedId(selectedId);
        if (selectedId) {
            const selectedOperation = operations.find(op => op.id === parseInt(selectedId));
            setOperation(selectedOperation);
        } else {
            setOperation({
                client_name: '',
                operation_name: '',
                business_link: '',
                description: '',
                financial_instrument: '',
                sector: '',
                sub_sector: '',
                country: ''
            });
        }
    };

    return (
        <div>
            {message && <p>{message}</p>}
            <form onSubmit={handleSubmit}>
                <label>
                    ID:
                    <select name="id" value={selectedId} onChange={handleSelectChange}>
                        <option value="">Seleccionar ID</option>
                        {operations.map(op => (
                            <option key={op.id} value={op.id}>{op.id}</option>
                        ))}
                    </select>
                </label>
                <br />
                <label>
                    Nombre del cliente:
                    <input type="text" name="nombre_cliente" value={operation.client_name} onChange={handleChange} />
                </label>
                <br />
                <label>
                    Nombre de la operación:
                    <input type="text" name="nombre_operacion" value={operation.operation_name} onChange={handleChange} />
                </label>
                <br />
                <label>
                    Link Portal del Negocio:
                    <input type="text" name="link_negocio" value={operation.business_link} onChange={handleChange} />
                </label>
                <br />
                <label>
                    Resumen de la operación:
                    <textarea name="descripcion" value={operation.description} onChange={handleChange}></textarea>
                </label>
                <br />
                <label>
                    Actividad Principal de la operación:
                    <select name="financial_instrument" value={operation.financial_instrument} onChange={handleChange}>
                        <option value="">Select Financial Instrument</option>
                        {financialInstruments.map((instrument, index) => (
                            <option key={index} value={instrument}>{instrument}</option>
                        ))}
                    </select>
                </label>
                <br/>
                <label>
                    Sector:
                    <input type="text" name="sector" value={operation.sector} onChange={handleChange}/>
                </label>
                <br/>
                <label>
                    Sub Sector:
                    <input type="text" name="sub_sector" value={operation.sub_sector} onChange={handleChange}/>
                </label>
                <br/>
                <label>
                    Country:
                    <input type="text" name="country" value={operation.country} onChange={handleChange} />
                </label>
                <br />
                <button type="submit">Submit</button>
            </form>
        </div>
    );
};

export default OperationForm;
