// src/utils.js
export const setNonApplicableFieldsToNull = (operation) => {
    const tipoCliente = operation.tipo_cliente;

    if (tipoCliente !== 'Financiamiento Estructurado') {
        operation.estructurado_contratacion_ingresos = null;
        operation.estructurado_capex_garantizado = null;
        operation.estructurado_financiamiento_identificado = null;
        operation.estructurado_permisos = null;
        operation.estructurado_estructura_ingresos = null;
        operation.estructurado_ingresos_publicos = null;
        operation.estructurado_experiencia_epecista = null;
        operation.estructurado_experiencia_operador = null;
    }
    if (tipoCliente !== 'Inversiones Patrimoniales') {
        operation.patrimoniales_rentabilidad_esperada = null;
        operation.patrimoniales_tipo_fondo = null;
        operation.patrimoniales_antecedentes_gestor = null;
        operation.patrimoniales_experiencia_sector = null;
        operation.patrimoniales_experiencia_region = null;
        operation.patrimoniales_experiencia_equipo = null;
    }
    if (tipoCliente !== 'Instituciones Financieras') {
        operation.financieras_morosidad = null;
        operation.financieras_solvencia = null;
    }

    return operation;
};
